import toast from "react-hot-toast";
import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../redux/Slices/CartSlice"


const Product = ({post}) => {
   
  const {cart} = useSelector((state) => state);
  const dispatch = useDispatch();

  const addtocart = () => {
    dispatch(add(post));
    toast.success("Item added To Cart");
  }

  const removefromcart = () => {
    dispatch(remove(post.id));
    toast.error("Item removed To Cart");
  }

  

  return (
    <div className="flex flex-col items-center justify-between
    hover:scale-110 transition-all duration-300 ease-in gap-3 p-4 mt-10 ml-5 rounded-xl outline ">

       <div className="text-gray-700 font-semibold text-lg truncate w-40 mt-1">
        <p>{post.title.split(" ").slice(0,3.5).join(" ")+ "..."}</p>
       </div>

       <div className="w-40 text-gray-400 font-normal text-[10px] text-left">
        <p>{post.description.split(" ").slice(0,10).join(" ") + "..."}</p>
       </div>
       <div className="h-[180px]">
        <img src={post.image} alt="" className="w-full h-full" />
       </div>

       <div className="flex justify-between gap-12 items-center">
        <div>
           <p className="text-green-600 font-semibold  w-full mt-5">${post.price}</p>
        </div>
       
        {
         cart.some((p) => p.id === post.id) ? 
         ( <button onClick={removefromcart}>
            Remove Item
          </button> ):
          (<button onClick={addtocart}>
            Add To Cart
          </button>)
        }
       
       </div>
       
    </div>
  );
};

export default Product;
