import toast from "react-hot-toast";
import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../redux/Slices/CartSlice";

const Product = ({ post }) => {
  const { cart } = useSelector((state) => state.Cart); // Make sure to match the slice name
  const dispatch = useDispatch();

  const addtocart = () => {
    if (!post || !post.id) {
      console.error("Invalid post object or post.id is missing");
      return;
    }
    dispatch(add(post));
    toast.success("Item added to cart");
  };

  const removefromcart = () => {
    if (!post || !post.id) {
      console.error("Invalid post object or post.id is missing");
      return;
    }
    dispatch(remove(post.id)); // Assuming `post.id` uniquely identifies the item
    toast.success("Item removed from cart");
  };

  return (
    <div>
      {post && (
        <>
          <div>
            <p>{post.title}</p>
          </div>
          <div>
            <p>{post.description}</p>
          </div>
          <div>
            <img src={post.image} alt="" />
          </div>
          <div>
            <p>{post.price}</p>
          </div>
          <div>
            <button onClick={cart.some((p) => p.id === post.id) ? removefromcart : addtocart}>
              {cart.some((p) => p.id === post.id) ? "Remove Item" : "Add To Cart"}
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Product;
